import React from "react";
import Head from "next/head";

const Footer = () => {
  return (
    <React.Fragment>
      <Head>
        <script
          src="https://kit.fontawesome.com/a40d908160.js"
          crossOrigin="anonymous"
        />
      </Head>
      <footer>
        <form>
          <h3>Subscribe Today!</h3>
          <input type="text" name="firstName" placeholder="First Name" />
          <input type="text" name="lastName" placeholder="Last Name" />
          <input type="text" name="email" placeholder="Email Address" />
          <button
            type="submit"
            onClick={(e) => {
              e.preventDefault();
            }}
          >
            SIGN UP
          </button>
        </form>
        <p>
          By signing up to our newsletter, you agree to receive promotional and
          transactional emails from us on a regular basis.
          <br />
          <br />
          We'll never give your email address to anyone else. You can view our
          privacy policy here.
          <br />
          <br />
          We'll send you emails about special offers, new products, free
          recipes, specialist advice and more. If you really want to, you can
          unsubscribe at any time.
        </p>
        <a href={"https://www.instagram.com/linghandmade18/"}>
          <i className="fab fa-instagram" />
        </a>
      </footer>

      <style jsx>{`
        footer {
          border-top: 1px solid #e4dbcc;
          margin-top: 8px;
          display: flex;
          justify-content: center;
          height: 250px;
        }

        form {
          margin-top: 30px;
          display: flex;
          flex-direction: column;
          justify-content: center;
          margin-bottom: 30px;
        }

        input {
          height: 30px;
          border-radius: 2px;
          border: 1px solid #d3d3d3;
          margin: 5px 0;
        }

        button {
          width: 90px;
          height: 30px;
          color: white;
          background: #92214a;
          border: 1px solid #92214a;
          border-radius: 2px;
        }

        p {
          width: 20%;
          font-size: 0.9rem;
          margin-top: 40px;
          margin-left: 4em;
          color: grey;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Footer;
